/*
 * API to retrieve the version of the CMSIS_RTOS_RTX5 APP
 */
DAVE_APP_VERSION_t CMSIS_RTOS_RTX5_GetAppVersion(void)
{
  DAVE_APP_VERSION_t version;

  version.major = (uint8_t)CMSIS_RTOS_RTX5_MAJOR_VERSION;
  version.minor = (uint8_t)CMSIS_RTOS_RTX5_MINOR_VERSION;
  version.patch = (uint8_t)CMSIS_RTOS_RTX5_PATCH_VERSION;

  return (version);
}

/**
 * @brief Function to initialize the cmsis_rtos_rtx5 APP.
 */
CMSIS_RTOS_RTX5_STATUS_t CMSIS_RTOS_RTX5_Init(CMSIS_RTOS_RTX5_t *const handle)
{
  /* CMSIS_RTOS_RTX_Init status*/
  CMSIS_RTOS_RTX5_STATUS_t status = CMSIS_RTOS_RTX5_STATUS_SUCCESS;
  /* Checking for null handler */
  XMC_ASSERT("CMSIS_RTOS_RTX5_Init: CMSIS_RTOS_RTX5 APP handle pointer uninitialized", (NULL != handle));
  /* Check APP initialization status to ensure whether CMSIS_RTOS_RTX5_Init called or not,
     initialize CMSIS_RTOS_RTX5 if CMSIS_RTOS_RTX5_Init called first time */
  if (handle->initialized == false)
  {
    if (osKernelInitialize() == osOK)
    {
      handle->initialized = true;
      status = CMSIS_RTOS_RTX5_STATUS_SUCCESS;
    }
    else
    {
      status = CMSIS_RTOS_RTX5_STATUS_FAILURE;
    }
  }

  return (status);
}
